// Simplest exponentiation test.

#include <gmp.h>

#define ITERS 500
#define N1 ""
#define N2 ""
#define N3 ""

int main(int argc, char** argv) {
	mpz_t x, y, z, w;
	mpz_init(x);
	mpz_init(y);
	mpz_init(z);
	mpz_init(w);
	mpz_set_str(x, N1, 16);
	mpz_set_str(y, N2, 16);
	mpz_set_str(z, N3, 16);
	int i;
	for (i = 0; i < ITERS; i++)
		mpz_powm(w, x, y, z);
	return 0;
}

